mapper.insert:
INSERT INTO MEMBER (
      MEMBER_ID
    , MEMBER_PW
    , MEMBER_EMAIL
    , MEMBER_NAME
    , MEMBER_NICKNAME
    , MEMBER_PHONE
    , ADDRESS
    , MEMBER_BIRTH
    , SALT
) VALUES (
      #{memberId}
    , #{memberPw}
    , #{memberEmail}
    , #{memberName}
    , #{memberNickname}
    , #{memberPhone}
    , #{address}
    , #{memberBirth}
    , #{salt}
)

mapper.select:
SELECT *
FROM MEMBER
WHERE 1=1
AND MEMBER_EMAIL = #{email}
AND MEMBER_NICKNAME = #{nickname}
AND SOCIAL_TYPE = 'KP'
